import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { 
  Bell, 
  MessageSquare, 
  User, 
  LogOut, 
  Menu, 
  X,
  Settings
} from 'lucide-react';
import { notificationService } from '../../services/notificationService';
import { messageService } from '../../services/messageService';

const Header = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [unreadNotifications, setUnreadNotifications] = useState(0);
  const [unreadMessages, setUnreadMessages] = useState(0);

  useEffect(() => {
    const fetchCounts = async () => {
      try {
        const notifResponse = await notificationService.getUnreadCount();
        const messageResponse = await messageService.getUnreadCount();
        setUnreadNotifications(notifResponse.count);
        setUnreadMessages(messageResponse.count);
      } catch (error) {
        console.error('Error fetching counts:', error);
      }
    };

    fetchCounts();
    const interval = setInterval(fetchCounts, 30000); // Rafraîchir toutes les 30 secondes

    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getRoleDisplay = (role) => {
    const roles = {
      'admin': 'Administrateur',
      'user_accueil': 'Agent Accueil',
      'user_standard': 'Agent Standard'
    };
    return roles[role] || role;
  };

  return (
    <header className="bg-white shadow-sm border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo et nom de l'application */}
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-sm">SG</span>
              </div>
              <span className="text-xl font-bold text-gray-900">
                SGED Mahajanga
              </span>
            </Link>
          </div>

          {/* Navigation desktop */}
          <nav className="hidden md:flex items-center space-x-8">
            {user?.role === 'admin' && (
              <>
                <Link to="/admin/dashboard" className="text-gray-700 hover:text-blue-600 font-medium">
                  Tableau de Bord
                </Link>
                <Link to="/admin/users" className="text-gray-700 hover:text-blue-600 font-medium">
                  Utilisateurs
                </Link>
                <Link to="/admin/circuits" className="text-gray-700 hover:text-blue-600 font-medium">
                  Circuits
                </Link>
                <Link to="/admin/statistiques" className="text-gray-700 hover:text-blue-600 font-medium">
                  Statistiques
                </Link>
              </>
            )}
            {(user?.role === 'user_accueil' || user?.role === 'user_standard') && (
              <>
                <Link to="/user/dashboard" className="text-gray-700 hover:text-blue-600 font-medium">
                  Tableau de Bord
                </Link>
                <Link to="/user/dossiers" className="text-gray-700 hover:text-blue-600 font-medium">
                  Dossiers
                </Link>
                <Link to="/user/circuits" className="text-gray-700 hover:text-blue-600 font-medium">
                  Circuits
                </Link>
                <Link to="/user/messages" className="text-gray-700 hover:text-blue-600 font-medium">
                  Messages
                </Link>
              </>
            )}
          </nav>

          {/* Actions utilisateur */}
          <div className="flex items-center space-x-4">
            {/* Notifications */}
            <Link 
              to="/notifications" 
              className="relative p-2 text-gray-600 hover:text-blue-600 transition-colors"
            >
              <Bell size={20} />
              {unreadNotifications > 0 && (
                <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                  {unreadNotifications}
                </span>
              )}
            </Link>

            {/* Messages */}
            <Link 
              to="/messages" 
              className="relative p-2 text-gray-600 hover:text-blue-600 transition-colors"
            >
              <MessageSquare size={20} />
              {unreadMessages > 0 && (
                <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                  {unreadMessages}
                </span>
              )}
            </Link>

            {/* Menu utilisateur */}
            <div className="relative">
              <button
                onClick={() => setIsMenuOpen(!isMenuOpen)}
                className="flex items-center space-x-2 p-2 rounded-lg hover:bg-gray-100 transition-colors"
              >
                <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                  <User size={16} className="text-blue-600" />
                </div>
                <div className="hidden md:block text-left">
                  <div className="text-sm font-medium text-gray-900">
                    {user?.prenom} {user?.nom}
                  </div>
                  <div className="text-xs text-gray-500">
                    {getRoleDisplay(user?.role)}
                  </div>
                </div>
              </button>

              {/* Dropdown menu */}
              {isMenuOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-50">
                  <Link
                    to="/profile"
                    className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    <User size={16} className="mr-3" />
                    Mon Profil
                  </Link>
                  {user?.role === 'admin' && (
                    <Link
                      to="/admin/settings"
                      className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      <Settings size={16} className="mr-3" />
                      Paramètres
                    </Link>
                  )}
                  <button
                    onClick={handleLogout}
                    className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-gray-100"
                  >
                    <LogOut size={16} className="mr-3" />
                    Déconnexion
                  </button>
                </div>
              )}
            </div>

            {/* Menu mobile */}
            <button
              className="md:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              {isMenuOpen ? <X size={20} /> : <Menu size={20} />}
            </button>
          </div>
        </div>

        {/* Menu mobile */}
        {isMenuOpen && (
          <div className="md:hidden py-4 border-t border-gray-200">
            <nav className="flex flex-col space-y-2">
              {user?.role === 'admin' && (
                <>
                  <Link 
                    to="/admin/dashboard" 
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Tableau de Bord
                  </Link>
                  <Link 
                    to="/admin/users" 
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Utilisateurs
                  </Link>
                  <Link 
                    to="/admin/circuits" 
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Circuits
                  </Link>
                  <Link 
                    to="/admin/statistiques" 
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Statistiques
                  </Link>
                </>
              )}
              {(user?.role === 'user_accueil' || user?.role === 'user_standard') && (
                <>
                  <Link
                    to="/user/dashboard"
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Tableau de Bord
                  </Link>
                  <Link
                    to="/user/dossiers"
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Dossiers
                  </Link>
                  <Link
                    to="/user/circuits"
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Circuits
                  </Link>
                  <Link
                    to="/user/messages"
                    className="px-4 py-2 text-gray-700 hover:bg-blue-50 hover:text-blue-600 rounded-lg"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    Messages
                  </Link>
                </>
              )}
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
